pragma solidity ^0.5.2;

import "./Ownable.sol";

contract Comment is Ownable {
    struct CommentInfo {
        bytes32 comment;
        address creator;
        address target;
        uint time;
    }

    CommentInfo public commentInfo;

    function initialize(address _owner, address _targetAddress, address _creatorAddress, bytes32 _comment) public {
        require(owner == address(0), "Nice try");
        owner = _owner;
        commentInfo.comment = _comment;
        commentInfo.creator = _creatorAddress;
        commentInfo.target = _targetAddress;
        commentInfo.time = now;
    }

    function deleteComment(bool _admin, bool _moderator, address _userAddress, address payable _suicideAddress) external onlyOwner {
        require(
            _admin || 
            _moderator || 
            _userAddress == commentInfo.creator, 
            "User does not have permission"
        );
        selfdestruct(_suicideAddress);
    }

    function getCommentCreator() public view returns(address) {
        return commentInfo.creator;
    }
}
