pragma solidity ^0.5.2;

import "./Comment.sol";
import "./CloneFactory.sol";

contract CommentFactory is CloneFactory {
    address cloneComment;

    constructor() public {
        cloneComment = address(new Comment());
    }

    function createComment (address _targetAddress, address _commentCreator, bytes32 _comment) 
    external returns(address) {
        address clone = createClone(cloneComment);
        Comment(clone).initialize(msg.sender, _targetAddress, _commentCreator, _comment);
        return clone;
    }
}
