pragma solidity ^0.5.2;

import "./Post.sol"; 
import "./PostFactory.sol";
import "./CommentFactory.sol";

contract Forum is Ownable {
    event UserBanned(address userAddress);
    event ModeratorCreated(address userAddress);

    mapping(address => ModeratorPermissions) public moderators;
    mapping(address => bool) public banned;
    mapping(address => bool) public posts;
    mapping(address => bool) public comments;

    uint public time;
    bytes32 public name;
    bytes32 public rules;
    bytes32 public description;
    address public creator;
    address[] public pinnedPosts;

    struct ModeratorPermissions {
        bool ban;
        bool editRules;
        bool editDescription;
        bool editModerator;
        bool editPinned;
        bool deletePost;
        bool deleteComment;
    }

    function initialize(bytes32 _forumName, address _owner, address _forumCreator) external {
        require(owner == address(0), "Nice try");
        owner = _owner;
        name = _forumName;
        time = now;
        creator = _forumCreator;
    }

    function createPost(bytes32 _post, address _postCreator, address _postFactory) external onlyOwner returns(address){
        require(!banned[_postCreator], "User is banned from this forum");
        address newPostAddress = PostFactory(_postFactory).createPost(_post, _postCreator);
        posts[newPostAddress] = true;
        return newPostAddress;
    }

    function deletePost(
        bool _admin, address _userAddress, address _postAddress, address payable _suicideAddress
    ) external onlyOwner {
        require(posts[_postAddress], "Post does not exist");
        Post(_postAddress).deletePost(_admin, moderators[_userAddress].deletePost, _userAddress, _suicideAddress);
        delete(posts[_postAddress]);
    }

    function pinPost(address _postAddress, address _userAddress) external onlyOwner {
        require(posts[_postAddress], "Post does not exist");
        require(
            moderators[_userAddress].editPinned, 
            "User does not have permission"
        );
        pinnedPosts.push(_postAddress);
    }

    function unpinPost(address _postAddress, uint _postIndex, address _userAddress) external onlyOwner {
        require(posts[_postAddress], "Post does not exist");
        require(
            moderators[_userAddress].editPinned, 
            "User does not have permission"
        );
        require(pinnedPosts.length > _postIndex, "Invalid index");
        require(pinnedPosts[_postIndex] == _postAddress, "Pinned post not found");
        pinnedPosts[_postIndex] = pinnedPosts[pinnedPosts.length - 1];
        pinnedPosts.length--;
    }

    function upvote(address _postAddress, address _userAddress) external onlyOwner {
        require(posts[_postAddress], "Post does not exist");
        require(!banned[_userAddress], "User is banned from this forum");
        Post(_postAddress).upvote();
    }

    function downvote(address _postAddress, address _userAddress) external onlyOwner {
        require(posts[_postAddress], "Post does not exist");
        require(!banned[_userAddress], "User is banned from this forum");
        Post(_postAddress).downvote();
    }

    function createComment(address _postAddress, address _targetAddress, address _commentCreator, bytes32 _comment, address _commentFactory) 
    external onlyOwner returns(address) {
        require(posts[_postAddress], "Post does not exist.");
        require(posts[_targetAddress] || comments[_targetAddress], "Target does not exist.");
        require(!banned[_commentCreator], "User is banned from this forum");
        address newCommentAddress = CommentFactory(_commentFactory).createComment(_targetAddress, _commentCreator, _comment);
        return newCommentAddress;
    }

    function deleteComment(
        bool _admin,
        address _userAddress,  
        address _postAddress, 
        address _commentAddress, 
        address payable _suicideAddress
    ) external onlyOwner {
        require(posts[_postAddress], "Post does not exist");
        require(comments[_commentAddress], "Comment does not exist");
        Comment(_commentAddress).deleteComment(_admin, moderators[_userAddress].deleteComment, _userAddress, _suicideAddress);
        comments[_commentAddress] = false;
    }

    function banUser(address _userAddress, address _moderatorAddress) external onlyOwner {
        require(!banned[_userAddress], "User already banned");
        require(moderators[_moderatorAddress].ban, "User does not have permission");
        banned[_userAddress] = true;
    }

    function unbanUser(address _userAddress, address _moderatorAddress) external onlyOwner {
        require(banned[_userAddress], "User not banned");
        require(moderators[_moderatorAddress].ban, "User does not have permission");
        delete banned[_userAddress];
    }

    function updateRules(bytes32 _rules, address _moderatorAddress) external onlyOwner {
        require (moderators[_moderatorAddress].editRules, "User does not have permission");
        rules = _rules;
    }

    function updateDescription(bytes32 _description, address _moderatorAddress) external onlyOwner {
        require (moderators[_moderatorAddress].editDescription, "User does not have permission");
        description = _description;
    }

    function createModerator(
        address _newModeratorAddress, 
        address _moderatorAddress,
        bool _ban, 
        bool _editRules, 
        bool _editDescription, 
        bool _editModerators, 
        bool _deletePost, 
        bool _deleteComment) external onlyOwner {
        require (moderators[_moderatorAddress].editModerator, "User does not have permission");
        ModeratorPermissions memory newModeratorPermissions;
        newModeratorPermissions.ban = _ban;
        newModeratorPermissions.editRules = _editRules;
        newModeratorPermissions.editDescription = _editDescription;
        newModeratorPermissions.editModerator = _editModerators;
        newModeratorPermissions.deletePost = _deletePost;
        newModeratorPermissions.deleteComment = _deleteComment;
        moderators[_newModeratorAddress] = newModeratorPermissions;
    }

    function removeModerator(address _targetModeratorAddress, address _moderatorAddress) external onlyOwner {
        require (moderators[_moderatorAddress].editModerator, "User does not have permission");
        delete moderators[_targetModeratorAddress];
    }

    function deleteForum(bool _admin, address _userAddress, address payable _suicideAddress) external onlyOwner {
        require(
            _admin || 
            _userAddress == creator, 
            "User does not have permission"
        );
        selfdestruct(_suicideAddress);
    }
}
