pragma solidity ^0.5.2;

import "./Forum.sol";
import "./CloneFactory.sol";

contract ForumFactory is CloneFactory {
    address cloneForum;

    constructor() public {
        cloneForum = address(new Forum());
    }

    function createForum(bytes32 _forumName, address _forumCreator) external returns(address) {
        address clone = createClone(cloneForum);
        Forum(clone).initialize(_forumName, msg.sender, _forumCreator);
        return clone;
    } 
}
