pragma solidity ^0.5.2;

import "./Ownable.sol";

contract Post is Ownable {
    struct PostInfo {
        bytes32 post;
        address creator;
        int32 votes;
        uint time;
    }  

    PostInfo public postInfo;

    function initialize(address _owner, bytes32 _post, address _postCreator) external {
        require(owner == address(0), "Nice try");
        owner = _owner;
        postInfo.post = _post;
        postInfo.creator = _postCreator;
        postInfo.time = now;
    }

    function upvote() external onlyOwner {
        postInfo.votes += 1;
    }

    function downvote() external onlyOwner {
        postInfo.votes -= 1;
    }

    function getPostCreator() public view returns(address) {
        return postInfo.creator;
    }

    function deletePost(bool _admin, bool _moderator, address _userAddress, address payable _suicideAddress) external onlyOwner {
        require(
            _admin || 
            _moderator || 
            _userAddress == postInfo.creator, 
            "User does not have permission");
        selfdestruct(_suicideAddress);
    }
}
