pragma solidity ^0.5.2;

import "./Comment.sol"; 
import "./Forum.sol"; 
import "./CloneFactory.sol";
import "./CommentFactory.sol";
import "./Ownable.sol";

contract Post is Ownable, CloneFactory {
    struct PostInfo {
        uint16 upvotes;
        uint16 downvotes;
        uint time;
        bytes32 post;
        address creator;
        mapping(address => bool) comments;
        mapping(address => bool) voters;
    }  

    PostInfo public postInfo;

    function initialize(address _owner, bytes32 _post, address _postCreator) external {
        require(owner == address(0), "Nice try");
        owner = _owner;
        postInfo.post = _post;
        postInfo.creator = _postCreator;
        postInfo.time = now;
    }

    function createComment (address _targetAddress, address _commentCreator, bytes32 _comment, address _commentFactory) 
    external onlyOwner returns(address) {
        require(_targetAddress == address(this) || postInfo.comments[_targetAddress], "Invalid comment target address");
        address newCommentAddress = CommentFactory(_commentFactory).createComment(_targetAddress, _commentCreator, _comment);
        return newCommentAddress;
    }

    function deleteComment(
        bool _admin, 
        bool _moderator, 
        address _userAddress, 
        address _commentAddress, 
        address payable _suicideAddress
    ) external onlyOwner {
        require(postInfo.comments[_commentAddress], "Comment not found");
        Comment(_commentAddress).deleteComment(_admin, _moderator, _userAddress, _suicideAddress);
    }

    function upvote(address _userAddress) external onlyOwner {
        require (!postInfo.voters[_userAddress], "Already voted");
        postInfo.voters[_userAddress] = true;
        postInfo.upvotes += 1;
    }

    function downvote(address _userAddress) external onlyOwner {
        require (!postInfo.voters[_userAddress], "Already voted");
        postInfo.voters[_userAddress] = true;
        postInfo.downvotes += 1;
    }

    function getPostCreator() public view returns(address) {
        return postInfo.creator;
    }

    function deletePost(bool _admin, bool _moderator, address _userAddress, address payable _suicideAddress) external onlyOwner {
        require(
            _admin || 
            _moderator || 
            _userAddress == postInfo.creator, 
            "User does not have permission");
        selfdestruct(_suicideAddress);
    }
}
