pragma solidity ^0.5.2;

import "./Post.sol";
import "./CloneFactory.sol";

contract PostFactory is CloneFactory {
    address clonePost;

    constructor() public {
        clonePost = address(new Post());
    }

    function createPost(bytes32 _post, address _postCreator) external returns(address){
        address clone = createClone(clonePost);
        Post(clone).initialize(msg.sender, _post, _postCreator);
        return clone;
    }
}
