pragma solidity ^0.5.2;

import "./User.sol"; 
import "./Post.sol"; 
import "./Forum.sol"; 
import "./Comment.sol";
import "./CloneFactory.sol";
import "./Ownable.sol";

contract Tartarus is Ownable, CloneFactory {
    event ForumCreated (address forumAddress);  
    event PostCreated (address indexed forumAddress, address indexed creatorAddress, address postAddress);
    event CommentCreated (address indexed postAddress, address indexed creatorAddress, address indexed targetAddress, address commentAddress);
    event ReportUser (address _username, bytes32 reason);
    event ReportForum (address forumAddress, bytes32 reason);
    event ReportPost (address indexed forumAddress, address postAddress, bytes32 reason);
    event ReportComment (address indexed forumAddress, address commentAddress, bytes32 reason);

    mapping (address => AdminPermissions) public admins;
    mapping (address => bool) public banned;
    mapping (bytes32 => address) public users;
    mapping (bytes32 => address) public forums;

    uint public transactionCost = 0;
    uint public createUserCost = 0;

    struct AdminPermissions {
        bool ban;
        bool transactionCost;
        bool createUserCost;
        bool editAdmin;
        bool deleteComment;
        bool deletePost;
        bool deleteForum;
    }

    address cloneForum;
    address clonePost;
    address cloneComment;
    address cloneUser;

    constructor() public {
        cloneForum = address(new Forum());
        clonePost = address(new Post());
        cloneComment = address(new Comment());
        cloneUser = address(new User());
    }

    function createUser(bytes32 _username) external payable {
        require((msg.value >= createUserCost), "Insufficient Eth Sent");
        require(users[_username] == address(0), "Username already exists");
        // require(validateUsername(_username), "Invalid username");
        address clone = createClone(cloneUser);
        User(clone).initialize(msg.sender, _username);
        users[_username] = clone;
    }

    function createForum(bytes32 _forumName) external {
        require(forums[_forumName] == address(0), "Forum already exists");
        require(users[User(msg.sender).username()] != address(0), "User not found");
        require(!banned[msg.sender], "User banned");
        address clone = createClone(cloneForum);
        Forum(clone).initialize(_forumName, msg.sender);
        forums[_forumName] = clone;
        emit ForumCreated(clone);
    }   

    function deleteForum(bytes32 _forumName, address payable _suicideAddress) external {
        require(users[User(msg.sender).username()] != address(0), "User not found");
        require(forums[_forumName] != address(0), "Forum does not exist");
        require(admins[msg.sender].deleteForum, "User does not have permission");
        Forum(forums[_forumName]).deleteForum(
            admins[users[User(msg.sender).username()]].deleteForum,
            msg.sender,
            _suicideAddress);
        banned[forums[_forumName]] = true;
        delete forums[_forumName];
    }   

    function createPost(bytes32 _forumName, bytes32 _post) external {
        require(users[User(msg.sender).username()] != address(0), "User not found");
        require(!banned[msg.sender], "User banned");
        require(forums[_forumName] != address(0), "Forum does not exist");
        address newPostAddress = Forum(forums[_forumName]).createPost(_post, msg.sender, clonePost);
        emit PostCreated(forums[_forumName], msg.sender, newPostAddress);
    }

    function deletePost(bytes32 _forumName, address _postAddress, address payable _suicideAddress) external {
        require(users[User(msg.sender).username()] != address(0), "User not found");
        require(forums[_forumName] != address(0), "Forum does not exist");
        require(admins[msg.sender].deletePost, "User does not have permission");
        Forum(forums[_forumName]).deletePost(
            admins[users[User(msg.sender).username()]].deletePost,
            msg.sender,
            _postAddress, 
            _suicideAddress 
        );
    }

    function pinPost(bytes32 _forumName, address _postAddress) external {
        require(users[User(msg.sender).username()] != address(0), "User not found");
        require(!banned[msg.sender], "User banned");
        require(forums[_forumName] != address(0), "Forum does not exist");
        Forum(forums[_forumName]).pinPost(_postAddress, msg.sender);
    }

    function unpinPost(bytes32 _forumName, address _postAddress, uint _postIndex) external {
        require(users[User(msg.sender).username()] != address(0), "User not found");
        require(!banned[msg.sender], "User banned");
        require(forums[_forumName] != address(0), "Forum does not exist");
        Forum(forums[_forumName]).unpinPost(_postAddress, _postIndex, msg.sender);
    }

    function upvote(bytes32 _forumName, address _postAddress) external {
        require(users[User(msg.sender).username()] != address(0), "User not found");
        require(!banned[msg.sender], "User banned");
        require(forums[_forumName] != address(0), "Forum does not exist");
        Forum(forums[_forumName]).upvote(_postAddress, msg.sender);
    }

    function downvote(bytes32 _forumName, address _postAddress) external {
        require(users[User(msg.sender).username()] != address(0), "User not found");
        require(!banned[msg.sender], "User banned");
        require(forums[_forumName] != address(0), "Forum does not exist");
        Forum(forums[_forumName]).downvote(_postAddress, msg.sender);
    }

    function createComment(bytes32 _forumName, address _postAddress, address _targetAddress, bytes32 _comment) external {
        require(users[User(msg.sender).username()] != address(0), "User not found");
        require(!banned[msg.sender], "User banned");
        require(forums[_forumName] != address(0), "Forum does not exist");
        address newCommentAddress = Forum(forums[_forumName]).createComment(_postAddress, _targetAddress, msg.sender, _comment, cloneComment);
        emit CommentCreated(_postAddress, msg.sender, _targetAddress, newCommentAddress);
    }

    function deleteComment(
        bytes32 _forumName, address _postAddress, address _commentAddress, address payable _suicideAddress) external {
        require(users[User(msg.sender).username()] != address(0), "User not found");
        require(forums[_forumName] != address(0), "Forum does not exist");
        require(admins[msg.sender].deleteComment, "User does not have permission");
        Forum(forums[_forumName]).deleteComment(
            admins[users[User(msg.sender).username()]].deleteComment,
            msg.sender,
            _postAddress, 
            _commentAddress, 
            _suicideAddress
        );
    }

    function createModerator(
        bytes32 _forumName,
        bytes32 _username, 
        bool _ban, 
        bool _editRules, 
        bool _editDescription, 
        bool _editModerators, 
        bool _deletePost, 
        bool _deleteComment) external {
        require(users[User(msg.sender).username()] != address(0), "User not found");
        require(forums[_forumName] != address(0), "Forum does not exist");
        Forum(forums[_forumName]).createModerator(
        users[_username],
        msg.sender,
        _ban, 
        _editRules,
        _editDescription,
        _editModerators,
        _deletePost,
        _deleteComment);
    }

    function removeModerator(bytes32 _forumName, bytes32 _username) external {
        require(users[User(msg.sender).username()] != address(0), "User not found");
        require(users[_username] != address(0), "Username does not exist");
        require(forums[_forumName] != address(0), "Forum does not exist");
        Forum(forums[_forumName]).removeModerator(users[_username], msg.sender);
    }

    function updateForumRules(bytes32 _forumName, bytes32 _rules) external onlyOwner {
        require(users[User(msg.sender).username()] != address(0), "User not found");
        require(forums[_forumName] != address(0), "Forum does not exist");
        Forum(forums[_forumName]).updateRules(_rules, msg.sender);
    }

    function updateForumDescription(bytes32 _forumName, bytes32 _description) external onlyOwner {
        require(users[User(msg.sender).username()] != address(0), "User not found");
        require(forums[_forumName] != address(0), "Forum does not exist");
        Forum(forums[_forumName]).updateDescription(_description, msg.sender);
    }

    function banUserForum(bytes32 _forumName, bytes32 _username) external {
        require(users[User(msg.sender).username()] != address(0), "User not found");
        require(users[_username] != address(0), "Username does not exist");
        require(forums[_forumName] != address(0), "Forum does not exist");
        Forum(forums[_forumName]).banUser(users[_username], msg.sender);
    }

    function unbanUserForum(bytes32 _forumName, bytes32 _username) external {
        require(users[User(msg.sender).username()] != address(0), "User not found");
        require(users[_username] != address(0), "Username does not exist");
        require(forums[_forumName] != address(0), "Forum does not exist");
        Forum(forums[_forumName]).unbanUser(users[_username], msg.sender);
    }

    function reportUser(bytes32 _username, bytes32 _reason) external {
        require(users[User(msg.sender).username()] != address(0), "User not found");
        require(users[_username] != address(0), "User not found");
        require(!banned[msg.sender], "User banned");
        emit ReportUser(users[_username], _reason);
    }

    function reportForum(bytes32 _forumName, bytes32 _reason) external {
        require(users[User(msg.sender).username()] != address(0), "User not found");
        require(forums[_forumName] != address(0), "Forum does not exist");
        require(!banned[msg.sender], "User banned");
        emit ReportForum(forums[_forumName], _reason);
    }

    function reportPost(bytes32 _forumName, address _postAddress, bytes32 _reason) external {
        require(users[User(msg.sender).username()] != address(0), "User not found");
        require(forums[_forumName] != address(0), "Forum does not exist");
        require(!banned[msg.sender], "User banned");
        emit ReportPost(forums[_forumName], _postAddress, _reason);
    }

    function reportComment(bytes32 _forumName, address _commentAddress, bytes32 _reason) external {
        require(users[User(msg.sender).username()] != address(0), "User not found");
        require(forums[_forumName] != address(0), "Forum does not exist");
        require(!banned[msg.sender], "User banned");
        emit ReportComment(forums[_forumName], _commentAddress, _reason);
    }

    function createAdmin(
        bytes32 _username, 
        bool _ban,
        bool _transactionCost,
        bool _createUserCost,
        bool _editAdmin,
        bool _deletePost,
        bool _deleteComment,
        bool _deleteForum
        ) public onlyOwner {
        require(users[User(msg.sender).username()] != address(0), "User not found");
        require(users[_username] != address(0), "User account not found");
        require(admins[msg.sender].editAdmin, "User does not have permission");
        // require(admins[users[_username]], "Target user is not admin"); ???
        AdminPermissions memory newAdminPermissions;
        newAdminPermissions.ban = _ban;
        newAdminPermissions.transactionCost = _transactionCost;
        newAdminPermissions.createUserCost = _createUserCost;
        newAdminPermissions.editAdmin = _editAdmin;
        newAdminPermissions.deleteComment = _deleteComment;
        newAdminPermissions.deletePost = _deletePost;
        newAdminPermissions.deleteForum = _deleteForum;
        admins[users[_username]] = newAdminPermissions;
    }  

    function removeAdmin(bytes32 _username) external {
        require(users[User(msg.sender).username()] != address(0), "User not found");
        require (msg.sender == owner || admins[msg.sender].editAdmin, "User does not have permission");
        // require(admins[users[_username]], "Target user is not admin"); ???
        delete admins[users[_username]];
    }

    function banUserTartarus(bytes32 _username) external view {
        require(users[User(msg.sender).username()] != address(0), "User not found");
        require(users[_username] != address(0), "Username does not exist");
        require(admins[msg.sender].ban, "User does not have permission");
        require(!banned[users[_username]], "User is already banned");
        banned[users[_username]] == true;
    }

    function unbanUserTartarus(bytes32 _username) external {
        require(users[User(msg.sender).username()] != address(0), "User not found");
        require(users[_username] != address(0), "Username does not exist");
        require(admins[msg.sender].ban, "User does not have permission");
        require(!banned[users[_username]], "User is not banned");
        delete banned[users[_username]];
    }

    function setCreateUserCost(uint _newCost) external {
        require(users[User(msg.sender).username()] != address(0), "User not found");
        require(admins[users[User(msg.sender).username()]].createUserCost, "User does not have permission");
        createUserCost = _newCost;
    }

    function setTransactionCost(uint _newCost) external {
        require(users[User(msg.sender).username()] != address(0), "User not found");
        require(admins[users[User(msg.sender).username()]].transactionCost, "User does not have permission");
        createUserCost = _newCost;
    }
}
